ruleContent.nextContentUrl:
@js:
if (result.indexOf("nextpage.png") > -1) {
	jsstr = result.match(/var jsstr ="(.*)";/)[1];
	jsarr = result.match(/var jsarr =\[(.*)\];/)[1].split(",");

	var nnstr = "";
	var nnarr = [];
	jsstr = String(java.base64Decode(jsstr));

	for (a = 0; a < jsstr.length; a++) {
		nnarr.push(jsstr[a]);
	}
	for (b = 0; b < jsarr.length; b++) {
		nnstr = nnstr + nnarr[jsarr[b]];
	}
	nnstr + "\.html"
}

